// Allow .scss imports
declare module '*.scss' {
  const value: string
  export = value
}

// Allow .png imports
declare module '*.png' {
  const value: string
  export = value
}

// Allow .jpg imports
declare module '*.jpg' {
  const value: string
  export = value
}

// Allow .svg imports
declare module '*.svg' {
  const value: string
  export = value
}
